const countnl = (str) => {
    const re = /[\n\r]/g
    return ((str || '').match(re) || []).length
}

const countother = (pattern, str) => {
    const re = pattern
    return ((str || '').match(re) || []).length
}

const extractData = function(inputData, inputPattern) {
    let tmp = inputData.match(inputPattern);
    if( tmp != null && tmp.length >= 2) {
        // successful match
        return tmp[1];
    }
    return "nomatch";
};



